%%%% método dos mínimos quadrados MMQ %%%%%%x=[2 6 4 8 12 10 18 0 16 14]';y=[4.70 4.84 5.02 5.38 5.47 5.61 5.95 4.01 5.90 5.80]';%%% ajustar uma reta pelo MMQA= [ones(size(x)) x] % só 1 na primeira coluna% ya_1 1 x_1% ya_2 1 x_2% ... = ...... c_1 c_2% ya_n 1 x_nc=A\y % solução de Ac=y representa reta ya_j=c_1+c_2 x_jya= c(1)+c(2)*x;plot(x,y,'r*',x,ya,'b')% calcular resíduoyaux=A*c;res=y-yaux;ResiduoMMQ=res'*res % res transposto * res